import React from 'react';
import { Label, FormGroup, Input, Form } from 'reactstrap';
import PropTypes from 'prop-types';

// import './LogIn.css';

export const InputBox = (props) => {

	return (
		<FormGroup style={{ bo }}>
			<Label>
				{props.label}
				{props.label && props.isReq && <span style={{ color: "red" }}> * </span>}
			</Label><br />
			<Form>
				<Row form>
					<Col md={6}>
						<FormGroup>
							<Label for="exampleEmail">Email</Label>
							<Input type="email" name="email" id="exampleEmail" placeholder="with a placeholder" />
						</FormGroup>
					</Col>
					<Col md={6}>
						<FormGroup>
							<Label for="examplePassword">Password</Label>
							<Input type="password" name="password" id="examplePassword" placeholder="password placeholder" />
						</FormGroup>
					</Col>
				</Row>
				<FormGroup>
					<Label for="exampleAddress">Address</Label>
					<Input type="text" name="address" id="exampleAddress" placeholder="1234 Main St" />
				</FormGroup>
				<FormGroup>
					<Label for="exampleAddress2">Address 2</Label>
					<Input type="text" name="address2" id="exampleAddress2" placeholder="Apartment, studio, or floor" />
				</FormGroup>
				<Row form>
					<Col md={6}>
						<FormGroup>
							<Label for="exampleCity">City</Label>
							<Input type="text" name="city" id="exampleCity" />
						</FormGroup>
					</Col>
					<Col md={4}>
						<FormGroup>
							<Label for="exampleState">State</Label>
							<Input type="text" name="state" id="exampleState" />
						</FormGroup>
					</Col>
					<Col md={2}>
						<FormGroup>
							<Label for="exampleZip">Zip</Label>
							<Input type="text" name="zip" id="exampleZip" />
						</FormGroup>
					</Col>
				</Row>
				<FormGroup check>
					<Input type="checkbox" name="check" id="exampleCheck" />
					<Label for="exampleCheck" check>Check me out</Label>
				</FormGroup>
				<Button>Sign in</Button>
			</Form>
			<Input
				className="form-control"
				title={props.label}
				placeholder={props.placeHolder}
				className={props.className}
				type={props.type}
				name={props.name}
				data-attribute={props.isReq}
				value={props.value}
				onChange={props.onChange}
				onBlur={props.onBlur}
				onClick={props.onClick}
			/><br />
			{props.errorMessage && <span style={{ color: "red" }}> {props.errorMessage} </span>}
		</FormGroup >
	);
}

InputBox.defaultProps = {
	isReq: false
}

InputBox.propTypes = {
	type: PropTypes.string.isRequired,                                                                                                    //when we declare any props which is Required then must be defined their "its default value".
	name: PropTypes.string.isRequired,
	value: PropTypes.string.isRequired,
	label: PropTypes.string,
	placeholder: PropTypes.oneOf(
		['number', 'string']
	)
}

export const CheckBox = (props) => {

	return (
		<FormGroup>
			<Label>
				{props.label}
				{props.isReq && <span style={{ color: "red" }}> * </span>}
			</Label><br />
			{
				props.list.map((item, i) => {
					return (
						<label key={i} className="mr-2">
							{
								props.checkOne
									?
									<input
										type="checkbox"
										name={props.name}
										title={props.label}
										value={item.value}
										data-attribute={props.isReq}
										checked={props.checkOne == item.value}
										onChange={props.onChange}
										onBlur={props.onBlur}
									/>
									: <input
										type="checkbox"
										name={props.name}
										title={props.label}
										value={item.value}
										data-attribute={props.isReq}
										onChange={props.onChange}
										onBlur={props.onBlur}
									/>
							}
							{item.label}
						</label>
					);
				})
			}
			<br />
			{props.errorMessage && <span style={{ color: "red" }}> {props.errorMessage} </span>}
		</FormGroup>
	);
}

export const MyRadio = (props) => {
	return (
		<FormGroup>
			<Label>
				{props.label}
				{props.isReq && <span style={{ color: "red" }}> * </span>}
			</Label><br />
			{props.list.map((item, i) => {
				return (
					<label key={i} className="mr-2">
						<input
							type="radio"
							name={props.name}
							title={props.label}
							value={item.value}
							data-attribute={props.isReq}
							onChange={props.onChange}
							onBlur={props.onBlur}
						/> {item.label}
					</label>
				);
			})}<br />
			{props.errorMessage && <span style={{ color: "red" }}> {props.errorMessage} </span>}
		</FormGroup>
	);
}


export const DropDown = (props) => {
	return (
		<FormGroup>
			<Label>
				{props.label}
				{props.isReq && <span style={{ color: "red" }}> * </span>}
			</Label><br />
			<select
				name={props.name}
				title={props.label}
				data-attribute={props.isReq}
				onChange={props.onChange}
				onBlur={props.onBlur}
			>
				<option value='' >Select...</option>
				{props.list.map((item, i) => {
					return (
						<option
							value={item.value}
						>	{item.label}</option>
					);
				})}
			</select><br />
			{props.errorMessage && <span style={{ color: "red" }}> {props.errorMessage} </span>}
		</FormGroup>
	);
}
